<template>
  <div class='rater'>
      <span v-for="(val,index) in slots" :key="index" :class="{gray: score<val*2,star: score>=val*2}"></span>
      <span class="score" v-show="showScore">{{score}}</span>
  </div>
</template>
<script>
export default {
    name: 'Rater',
    props: {
        rate: Number,
        showScore: {
            type: Boolean,
            default: true
        }
    },
    data () {
        return {
            slots: [1,2,3,4,5],
            score: this.rate
        }
    },
    mounted () {
        if(!this.rate && this.rate !== 0){
            this.slots = null
            this.score = '暂无评分'
        }
        else{
            this.slots = [1,2,3,4,5]
            this.score = this.rate
        }
    }
}
</script>
<style lang="less">
.rater{
    span{
        display: inline-block;
        width: 10px;
        height: 10px;
        background-size: 10px 10px;
        font-size: .72rem;
    }
    .score{
        color: #aaa;
    }
    .gray{
        background-image: url(data:image/png;base64,)
    }
    .star{
        background-image: url(data:image/png;base64,)
    }
}
</style>
